class NewsRespones {
  NewsRespones({
      this.status, 
      this.totalResults, 
      this.articles,});

  NewsRespones.fromJson(dynamic json) {
    status = json['status'];
    status = json['code'];
    status = json['message'];
    totalResults = json['totalResults'];
    if (json['articles'] != null) {
      articles = [];
      json['articles'].forEach((v) {
        articles?.add(News.fromJson(v));
      });
    }
  }
  String? status;
  String? code;
  String? message;
  int? totalResults;
  List<News>? articles;
NewsRespones copyWith({  String? status,
  int? totalResults,
  List<News>? articles,
}) => NewsRespones(  status: status ?? this.status,
  totalResults: totalResults ?? this.totalResults,
  articles: articles ?? this.articles,
);
  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['status'] = status;
    map['totalResults'] = totalResults;
    if (articles != null) {
      map['articles'] = articles?.map((v) => v.toJson()).toList();
    }
    return map;
  }

}